
public class SpecificNumbers {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int number1;
		int number = 0; 
		
		number1 = 1;
		
	do {
		System.out.println(" ");
		System.out.println(number1 + number);
		number = number1 + number;
	}
		while (number < 40);
	}

}
