
public class MathClass {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number1;
		int number2;
		int result = 0;
		
		//assigning
		number1 = result;
		number2 = result - 2;
		result = number1 * number2;
		
		System.out.println(number1 + " * " + number2 + " = " + result);
	}

}
